mapper.select:

select 'false' as QUERYID,

from commodity
where id = #{id,jdbcType=BIGINT}

select 'false' as QUERYID,
from commodity where status = 1 order by category_id
limit #{offset, jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

mapper.update:
update commodity
set status = 2
where category_id = #{categoryId,jdbcType=BIGINT}

mapper.select:
select 'false' as QUERYID,

from commodity
where status = 1 and name = #{name,jdbcType=VARCHAR} and brand = #{brand,jdbcType=VARCHAR}

select 'false' as QUERYID,

from commodity
where status = 1 and name like #{name,jdbcType=VARCHAR}
